import React, { useState } from 'react';
import './App.css';

// Datos de ejemplo
const datos = [
  { numeroPasajero: "Pasajero 1", nombre: 'Juan Pablo Castro', asiento: '', especificaciones: 'No Asignado' },
  { numeroPasajero: "Pasajero 2", nombre: 'Jhon Alejandro Garcia', asiento: '', especificaciones: 'No Asignado' },
];

// Componente Modal
const Modal = ({ isOpen, onClose, pasajero }) => {
  if (!isOpen) return null;

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <h2>Asiento de {pasajero.nombre}</h2>
        <select value={pasajero.especificaciones} onChange={(e) => handleEspecificacionesChange(e, pasajero.numeroPasajero)}>
          <option value="No Asignado">No Asignado</option>
          <option value="Ventana">Ventana</option>
          <option value="Pasillo">Pasillo</option>
          {/* Agrega más opciones según sea necesario */}
        </select>
        <button onClick={onClose}>Cerrar</button>
      </div>
    </div>
  );
};

// Función principal del componente React
function App() {
  const [modalIsOpen, setModalIsOpen] = useState(false);
  const [selectedPasajero, setSelectedPasajero] = useState(null);

  const openModal = (pasajero) => {
    setSelectedPasajero(pasajero);
    setModalIsOpen(true);
  };

  const closeModal = () => setModalIsOpen(false);

  const handleEspecificacionesChange = (e, numeroPasajero) => {
    const nuevoValor = e.target.value;
    // Actualiza el estado de datos o realiza otras acciones según sea necesario
    // Por ejemplo, puedes usar el número de pasajero para identificar y actualizar el estado correspondiente
  };

  return (
    <html>
      <head>
        <link rel="stylesheet" href="App.css" />
        <title>Visualización básica en tiempo real de disponibilidad de asientos</title>
      </head>
      <body>
        {/* Encabezado */}
        <header className="primero">
          <div className="titulo-res">
            <h1 className="titulo-reserva"> # de Numero de reserva </h1>
          </div>
          <div className="pasajeros">
            <h4 className="pasajeros-titulo">Cantidad de pasajeros: {datos.length}</h4>
          </div>
        </header>

        {/* Segundo encabezado */}
        <header className="segundo">
          <div className="titulo-listado">
            <div className="listado">
              <h3 className="titulo-listado-pasajeros">Listado de pasajeros</h3>
            </div>
          </div>
        </header>

        {/* Línea horizontal */}
        <hr style={{ borderColor: "#2196F3", width: "95%" }} />

        {/* Contenedor de la tabla */}
        <div className='tabla'>
          <table>
            <tbody>
              {/* Mapeo de datos para crear filas de la tabla */}
              {datos.map((pasajero) => (
                <tr key={pasajero.numeroPasajero}>
                  {/* Celda con imagen */}
                  <td>
                    <img
                      src={process.env.PUBLIC_URL + '/Images/usuario.png'}
                      style={{ width: '40px', height: '40px' }} 
                    />
                  </td>
                  {/* Celda con nombre y número de pasajero */}
                  <td>
                    <h3>{pasajero.nombre}</h3>
                    <h6>{pasajero.numeroPasajero}</h6>
                  </td>
                  {/* Celda con botón para seleccionar asiento */}
                  <td><button onClick={() => openModal(pasajero)}>Seleccionar asiento</button></td>
                  {/* Celda con especificaciones del asiento */}
                  <td>
                    <div className='asignación'>
                      <h3>{pasajero.especificaciones}</h3>
                    </div>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
          <div className='volver'>
            <button>Volver</button>
          </div>
        </div>

        {/* Ventana flotante (Modal) */}
        <Modal isOpen={modalIsOpen} onClose={closeModal} pasajero={selectedPasajero} />
      </body>
    </html>
  );
}

export default App;
