// seleccionar_asiento.js
import React, { useState } from 'react';
import './seleccionar_asiento.css';

const SeleccionarAsiento = ({ onClose, pasajero }) => {
  const [asientoSeleccionado, setAsientoSeleccionado] = useState('');

  const handleAsientoSeleccionado = (silla) => {
    // Si el mismo asiento ya está seleccionado, deselecciónalo
    if (asientoSeleccionado === silla) {
      setAsientoSeleccionado('');
    } else {
      // Si otro asiento está seleccionado, reemplázalo
      setAsientoSeleccionado(silla);
    }
  };

  // Datos quemados para la tabla
  const datosAsientos = [
    { silla: '1A', clase: 'Primera Clase', ubicacion: 'Ventana' },
    { silla: '15B', clase: 'Económica', ubicacion: 'Pasillo' },
    { silla: '8C', clase: 'Business', ubicacion: 'Ventana' },
  ];

  return (
    <div className="seleccionar_asiento-overlay">
      <div className="seleccionar_asiento-content">
        <h6>Pasajero: {pasajero.numeroPasajero}</h6>
        <h6>{pasajero.numeroPasajero} reserva</h6>
        <div>
          <h3>{pasajero.nombre}</h3>
        </div>

        <table>
  <thead>
    <tr>
      <th> </th>
      <th>Silla</th>
      <th>Clase</th>
      <th>Ubicación</th>
    </tr>
  </thead>
  <tbody>
    {/* Fila adicional para el hr debajo de las etiquetas de las columnas */}
    <tr>
      <td colSpan="4">
        <hr style={{ borderColor: "#2196F3", width: "98%" }} />
      </td>
    </tr>
    {datosAsientos.map((asiento, index) => (
      <tr key={index}>
        <td>
          <input
            type="radio"
            name="asiento"
            checked={asientoSeleccionado === asiento.silla}
            onChange={() => handleAsientoSeleccionado(asiento.silla)}
          />
        </td>
        <td>{asiento.silla}</td>
        <td>{asiento.clase}</td>
        <td>{asiento.ubicacion}</td>
      </tr>
    ))}
  </tbody>
</table>

        <button onClick={onClose}>Cancelar</button>
        <button onClick={() => onClose(asientoSeleccionado)}>Confirmar</button>
      </div>
    </div>
  );
};

export default SeleccionarAsiento;
