// App.js
import React, { useState } from 'react';
import SeleccionarAsiento from './componentes'; // Ajusta la ruta según la ubicación real de tu componente
import './App.css';

const datos = [
  { numeroPasajero: "Pasajero 1", nombre: 'Juan Pablo Castro', asiento: '', especificaciones: 'No Asignado' },
  { numeroPasajero: "Pasajero 2", nombre: 'Jhon Alejandro Garcia', asiento: '', especificaciones: 'No Asignado' },
];

const App = () => {
  const [modalVisible, setModalVisible] = useState(false);
  const [pasajeroSeleccionado, setPasajeroSeleccionado] = useState(null);

  const openModal = (pasajero) => {
    setPasajeroSeleccionado(pasajero);
    setModalVisible(true);
  };

  const closeModal = () => setModalVisible(false);

  return (
    <div>
      {/* ... (tu código existente) */}

      <div className='tabla'>
        <table>
          <tbody>
            {datos.map((pasajero) => (
              <tr key={pasajero.numeroPasajero}>
                {/* ... (tu código existente) */}
                <td>
                  <button onClick={() => openModal(pasajero)}>Seleccionar asiento</button>
                </td>
                {/* ... (tu código existente) */}
              </tr>
            ))}
          </tbody>
        </table>
        {modalVisible && <SeleccionarAsiento onClose={closeModal} pasajero={pasajeroSeleccionado} />}
      </div>
    </div>
  );
}

export default App;
