// seleccionar_asiento.js
import React, { useState } from 'react';
import './seleccionar_asiento.css';

const SeleccionarAsiento = ({ onClose, pasajero }) => {
  const [asientosSeleccionados, setAsientosSeleccionados] = useState([]);

  const handleAsientoSeleccionado = (silla) => {
    // Verifica si el asiento ya está seleccionado
    const isSelected = asientosSeleccionados.includes(silla);

    if (isSelected) {
      // Si ya está seleccionado, quítalo de la lista
      setAsientosSeleccionados(asientosSeleccionados.filter((asiento) => asiento !== silla));
    } else {
      // Si no está seleccionado, agrégalo a la lista
      setAsientosSeleccionados([...asientosSeleccionados, silla]);
    }
  };

  // Datos quemados para la tabla
  const datosAsientos = [
    { silla: '1A', clase: 'Primera Clase', ubicacion: 'Ventana' },
    { silla: '15B', clase: 'Económica', ubicacion: 'Pasillo' },
    { silla: '8C', clase: 'Business', ubicacion: 'Ventana' },
  ];

  return (
    <div className="seleccionar_asiento-overlay">
      <div className="seleccionar_asiento-content">
        <h6>Pasajero: {pasajero.numeroPasajero}</h6>
        <h6>{pasajero.numeroPasajero} reserva</h6>
        <div>
          <h3>{pasajero.nombre}</h3>
        </div>

        <table>
          <thead>
            <tr>
              <th>Seleccionar</th>
              <th>Silla</th>
              <th>Clase</th>
              <th>Ubicación</th>
            </tr>
          </thead>
          <tbody>
            {datosAsientos.map((asiento, index) => (
              <tr key={index}>
                <td>
                  <input
                    type="checkbox"
                    checked={asientosSeleccionados.includes(asiento.silla)}
                    onChange={() => handleAsientoSeleccionado(asiento.silla)}
                  />
                </td>
                <td>{asiento.silla}</td>
                <td>{asiento.clase}</td>
                <td>{asiento.ubicacion}</td>
              </tr>
            ))}
          </tbody>
        </table>

        <button onClick={onClose}>Cancelar</button>
        <button onClick={() => onClose(asientosSeleccionados)}>Confirmar</button>
      </div>
    </div>
  );
};

export default SeleccionarAsiento;
