import React, { useState } from 'react';
import './App.css';

function App() {
  const [pasajeros, setPasajeros] = useState([]);

  const handleAñadirPasajero = () => {
    const nuevoPasajero = {
      nombre: 'Nombre del pasajero',
      asiento: '',
    };

    setPasajeros([...pasajeros, nuevoPasajero]);
  };

  return (
    <html>
      <head>
        <link rel="stylesheet" href="App.css" />
        <title>Visualización básica en tiempo real de disponibilidad de asientos</title>
      </head>
      <body>
        <header className="primero">
          <div className="titulo-res">
            <h1 className="titulo-reserva"> # de Numero de reserva </h1>
          </div>
          <div className="pasajeros">
            <h4 className="pasajeros-titulo"> Cantidad de pasajeros : </h4>
            <h4 className="pasajeros-cantidad"> # </h4>
          </div>
        </header>
        <header className="segundo">
          <div className="titulo-listado">
            <div className="listado">
              <h3 className="titulo-listado-pasajeros">Listado de pasajeros</h3>
            </div>
          </div>
        </header>
        <hr style={{ borderColor: "#2196F3", width: "95%" }} />

        <button onClick={handleAñadirPasajero}>Añadir pasajero</button>

        <table>
          <tbody>
            {pasajeros.map((pasajero, index) => (
              <tr key={index}>
                <td><img src="ruta_de_la_imagen_fija" alt="Imagen de pasajero" /></td>
                <td>{pasajero.nombre}</td>
                <td><button>Seleccionar asiento</button></td>
                <td>{/* Especificaciones del asiento */}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </body>
    </html>
  );
}

export default App;
