// seleccionar_asiento.js
import React, { useState } from 'react';
import './seleccionar_asiento.css';

const SeleccionarAsiento = ({ onClose, pasajero }) => {
  const [asientoSeleccionado, setAsientoSeleccionado] = useState('');

  const handleAsientoSeleccionado = (asiento) => {
    // Puedes realizar cualquier lógica adicional aquí, por ejemplo, validar la disponibilidad del asiento
    setAsientoSeleccionado(asiento);
  };

  return (
    <div className="seleccionar_asiento-overlay">
      <div className="seleccionar_asiento-content">
        <h6>Pasajero: {pasajero.numeroPasajero}</h6>
        <h6>{pasajero.numeroPasajero} reserva</h6>
        <div>
          <h3>{pasajero.nombre}</h3>
        </div>

        <table>
          <thead>
            <tr>
              <th>Silla</th>
              <th>Clase</th>
              <th>Ubicación</th>
              <th>Seleccionar</th>
            </tr>
          </thead>
          <tbody>
            {/* Datos de ejemplo para las filas de la tabla */}
            <tr>
              <td>1A</td>
              <td>Primera Clase</td>
              <td>Ventana</td>
              <td><button onClick={() => handleAsientoSeleccionado('1A')}>Seleccionar</button></td>
            </tr>
            <tr>
              <td>15B</td>
              <td>Económica</td>
              <td>Pasillo</td>
              <td><button onClick={() => handleAsientoSeleccionado('15B')}>Seleccionar</button></td>
            </tr>
            <tr>
              <td>8C</td>
              <td>Business</td>
              <td>Ventana</td>
              <td><button onClick={() => handleAsientoSeleccionado('8C')}>Seleccionar</button></td>
            </tr>
          </tbody>
        </table>

        <button onClick={onClose}>Cancelar</button>
        <button onClick={onClose}>Confirmar</button>
      </div>
    </div>
  );
};

export default SeleccionarAsiento;
